	let data = book[type]
	let value
	switch (type) {
		case 'pornrate':
			value = data.split("%")[0]
			break;
		case 'update':
			value = parseInt(data.replace(/-/g,""))
			break;
		case 'word':
			m = data.match(/[^\d.]+/)
			f = 1
			if (m&&/k/i.test(m)) {
				f = 1000
			}else if(m&&/m/i.test(m)) {
				f = 1000000
			}
			value = Number(data.match(/[\d.]+/))*f
			break;
		default:
			value = data
	}
	return value
}
function spArr(arr, num) {
	let newArr = []
	for(let i = 0; i < arr.length;){
		newArr.push(arr.slice(i, i += num))
	}
	return newArr
}
